import * as fastify from 'fastify';
import { FastifyRequest } from 'fastify';

type FastifyRequestLoggerOptions = {
    logBody?: boolean;
    logResponseTime?: boolean;
    logBindings?: Record<string, unknown>;
    ignoredPaths?: Array<string | RegExp>;
    ignoredBindings?: Record<string, unknown>;
    ignore?: (request: FastifyRequest) => boolean;
};

declare const _default: fastify.FastifyPluginAsync<FastifyRequestLoggerOptions>;

export { type FastifyRequestLoggerOptions, _default as default };
